import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import FileList from './components/FileList';
import './styles/styles.css';

const App = () => {
  const [files, setFiles] = useState([]);
  const [selectedFileContent, setSelectedFileContent] = useState('');
  const [reviewResult, setReviewResult] = useState('');

  // Define the handleFileUpload function
  const handleFileUpload = (newFiles) => {
    setFiles((prevFiles) => [...prevFiles, ...newFiles]); // Append new files to the existing list
  };

  const handleCodeReview = async (file) => {
    const reader = new FileReader();

    reader.onload = async (event) => {
      const fileContent = event.target.result;
      setSelectedFileContent(fileContent); // Display the file content in the UI

      // Azure OpenAI API details
      const endpoint = "https://ragdemo010.openai.azure.com";
      const apiKey = "1fdjJbIEYDvpFzDnTckMJVRNmGtPnNr4qoHDehaHNX8qn1I5vB2RJQQJ99BCACYeBjFXJ3w3AAABACOGK7WH";
      const deploymentName = "gpt-4o"; // e.g., "text-davinci-003"
      //https://ragdemo010.openai.azure.com/openai/deployments/gpt-4o/chat/completions?api-version=2025-01-01-preview

      try {
        const response = await fetch(`${endpoint}/openai/deployments/${deploymentName}/completions?api-version=2025-01-01-preview`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'api-key': apiKey, // Azure OpenAI requires 'api-key' header
          },
          body: JSON.stringify({
            prompt: `Review the following code:\n\n${fileContent}`,
            max_tokens: 500,
          }),
        });

        if (!response.ok) {
          throw new Error(`Azure OpenAI API error: ${response.statusText}`);
        }

        const data = await response.json();
        setReviewResult(data.choices[0].text); // Display the review result in the UI
      } catch (error) {
        console.error("Error during code review:", error);
        alert("Failed to perform code review. Please check the console for details.");
      }
    };

    reader.readAsText(file); // Read the file content as text
  };

  return (
    <div className="App">
      <h1>File Upload and Code Review</h1>
      <FileUpload onFileUpload={handleFileUpload} />
      <FileList files={files} onCodeReview={handleCodeReview} />

      {/* Display the selected file content */}
      {selectedFileContent && (
        <div className="file-content">
          <h2>File Content:</h2>
          <pre>{selectedFileContent}</pre>
        </div>
      )}

      {/* Display the code review result */}
      {reviewResult && (
        <div className="review-result">
          <h2>Code Review Result:</h2>
          <pre>{reviewResult}</pre>
        </div>
      )}
    </div>
  );
};

export default App;
